     1→/* eslint-disable react-hooks/exhaustive-deps */
     2→import { Alert, Platform, TouchableOpacity, StyleSheet } from "react-native";
     3→
     4→import { MemoizedExpenseItem } from "./ExpenseItem";
     5→import React, {
     6→  useCallback,
     7→  useContext,
     8→  useEffect,
     9→  useMemo,
    10→  useRef,
    11→} from "react";
    12→import { View } from "react-native";
    13→
    14→import Swipeable from "react-native-gesture-handler/Swipeable";
    15→import { GestureHandlerRootView } from "react-native-gesture-handler";
    16→import { GlobalStyles, ListLayoutAnimation } from "../../constants/styles";
    17→import {
    18→  fetchTripName,
    19→  getAllExpenses,
    20→  touchAllTravelers,
    21→} from "../../util/http";
    22→import { TripContext } from "../../store/trip-context";
    23→import { ExpensesContext } from "../../store/expenses-context";
    24→import IconButton from "../UI/IconButton";
    25→import Animated, {
    26→  Easing,
    27→  FadeIn,
    28→  FadeInLeft,
    29→  FadeInRight,
    30→  FadeInUp,
    31→  FadeOutLeft,
    32→  FadeOutRight,
    33→  FadeOutUp,
    34→} from "react-native-reanimated";
    35→
    36→//Localization
    37→import * as Localization from "expo-localization";
    38→import { I18n } from "i18n-js";
    39→import { en, de, fr, ru } from "../../i18n/supportedLanguages";
    40→import {
    41→  deleteExpenseOnlineOffline,
    42→  OfflineQueueManageExpenseItem,
    43→  storeExpenseOnlineOffline,
    44→} from "../../util/offline-queue";
    45→import {
    46→  ExpenseData,
    47→  Expense,
    48→  deleteAllExpensesByRangedId,
    49→} from "../../util/expense";
    50→import PropTypes from "prop-types";
    51→import { NetworkContext } from "../../store/network-context";
    52→import { Toast } from "react-native-toast-message/lib/src/Toast";
    53→import { useNavigation } from "@react-navigation/native";
    54→import { useState } from "react";
    55→import { Text } from "react-native-paper";
    56→import { formatExpenseWithCurrency } from "../../util/string";
    57→import { addShadowItemsToExpenses } from "./ExpenseListUtil";
    58→import { UserContext } from "../../store/user-context";
    59→import * as Haptics from "expo-haptics";
    60→import { toShortFormat } from "../../util/date";
    61→import {
    62→  DEVELOPER_MODE,
    63→  EXPENSES_LOAD_TIMEOUT,
    64→  MAX_EXPENSES_RENDER,
    65→} from "../../confAppConstants";
    66→import { TripAsObject } from "../../screens/TripSummaryScreen";
    67→import { Pressable } from "react-native";
    68→import safeLogError from "../../util/error";
    69→import { constantScale, dynamicScale } from "../../util/scalingUtil";
    70→const i18n = new I18n({ en, de, fr, ru });
    71→i18n.locale = ((Localization.getLocales()[0]&&Localization.getLocales()[0].languageCode)?Localization.getLocales()[0].languageCode.slice(0,2):'en');
    72→i18n.enableFallback = true;
    73→
    74→// Displays a list of all expenses.
    75→function ExpensesList({
    76→  expenses,
    77→  showSumForTravellerName,
    78→  isFiltered,
    79→  refreshControl,
    80→  refreshing,
    81→}) {
    82→  // GLOBALS across all expenseItems
    83→  let tripID = "";
    84→  // let expenseCtx;
    85→  let filtered = false;
    86→  // let navigation;
    87→  const row = useMemo(() => [], []);
    88→  const prevOpenedRow = useRef(null);
    89→  let travellerName = "";
    90→  const navigation = useNavigation();
    91→
    92→  const { isConnected, strongConnection } = useContext(NetworkContext);
    93→  const isOnline = isConnected && strongConnection;
    94→  const { tripid, tripName } = useContext(TripContext);
    95→  const { periodName } = useContext(UserContext);
    96→  const expenseCtx = useContext(ExpensesContext);
    97→  const userCtx = useContext(UserContext);
    98→  tripID = tripid;
    99→  travellerName = showSumForTravellerName;
   100→  if (isFiltered) filtered = true;
   101→  const multipleTripsInHistory =
   102→    userCtx.tripHistory && userCtx.tripHistory.length > 1;
   103→
   104→  const [selectable, setSelectable] = useState(false);
   105→  const [selected, setSelected] = useState<string[]>([]);
   106→  // autoscroll to the first item after the header
   107→  const flatListRef = useRef(null);
   108→  // for scroll to top
   109→  const [contentVerticalOffset, setContentVerticalOffset] = useState(0);
   110→  const CONTENT_OFFSET_THRESHOLD = 300;
   111→  const showScrollToTop = contentVerticalOffset > CONTENT_OFFSET_THRESHOLD;
   112→
   113→  if (expenses?.length < 6) addShadowItemsToExpenses(expenses);
   114→
   115→  const forceCloseRow = useCallback(
   116→    (index) => {
   117→      try {
   118→        row[index].close();
   119→      } catch (error) {
   120→        // console.log("forceCloseRow ~ error", error);
   121→      }
   122→    },
   123→    [row]
   124→  );
   125→  const closeRow = useCallback(
   126→    (index) => {
   127→      if (prevOpenedRow.current && prevOpenedRow.current !== row[index]) {
   128→        prevOpenedRow.current.close();
   129→      }
   130→      prevOpenedRow.current = row[index];
   131→
   132→      setTimeout(() => {
   133→        forceCloseRow(index);
   134→      }, 1500);
   135→    },
   136→    [forceCloseRow, row]
   137→  );
   138→
   139→  const renderRightActions = useCallback((progress, dragX, onClick) => {
   140→    return (
   141→      <View
   142→        style={{
   143→          marginBottom: dynamicScale(2, true, 0.5),
   144→          paddingTop: dynamicScale(14, true, 0.5),
   145→          paddingLeft: dynamicScale(10, true, 0.5),
   146→          alignContent: "center",
   147→          justifyContent: "center",
   148→          width: dynamicScale(56),
   149→          backgroundColor: GlobalStyles.colors.error500,
   150→        }}
   151→      >
   152→        {/* <Text>test</Text> */}
   153→        <IconButton
   154→          icon="trash"
   155→          color={GlobalStyles.colors.backgroundColor}
   156→          size={constantScale(36, 0.5)}
   157→          onPress={onClick}
   158→          buttonStyle={{
   159→            marginBottom: "0%",
   160→            marginTop: "-30%",
   161→            marginLeft: "-20%",
   162→          }}
   163→        />
   164→      </View>
   165→    );
   166→  }, []);
   167→  const onClick = useCallback(({ item, index }, isOnline) => {
   168→    const editedExpenseId = item.id;
   169→    const uid = item.uid;
   170→    async function deleteAllExpenses() {
   171→      try {
   172→        navigation?.popToTop();
   173→        Toast.show({
   174→          type: "loading",
   175→          text1: i18n.t("toastDeleting1"),
   176→          text2: i18n.t("toastDeleting2"),
   177→          autoHide: false,
   178→        });
   179→        const allExpenses = isOnline
   180→          ? await getAllExpenses(tripID)
   181→          : expenseCtx?.expenses;
   182→        for (let i = 0; i < allExpenses?.length; i++) {
   183→          const expense = allExpenses[i];
   184→          if (expense?.rangeId == item?.rangeId) {
   185→            // console.log("found a ranged id match", expense?.rangeId);
   186→            const queueItem: OfflineQueueManageExpenseItem = {
   187→              type: "delete",
   188→              expense: {
   189→                tripid: tripID,
   190→                uid: expense.uid,
   191→                id: expense.id,
   192→              },
   193→            };
   194→            expenseCtx?.deleteExpense(expense.id);
   195→            await deleteExpenseOnlineOffline(queueItem, isOnline);
   196→          }
   197→        }
   198→        await touchAllTravelers(tripID, true);
   199→        Toast.hide();
   200→      } catch (error) {
   201→        // console.log("delete All Error:", error);
   202→        Toast.show({
   203→          text1: i18n.t("error"),
   204→          text2: i18n.t("error2"),
   205→          type: "error",
   206→        });
   207→      }
   208→    }
   209→
   210→    async function deleteExp() {
   211→      if (item.rangeId) {
   212→        // do you want to delete one or all expenses with this rangeId?
   213→        Alert.alert(
   214→          `Delete grouped range expenses?`,
   215→          `This will delete all entries that belong to ${
   216→            item.description
   217→          } for ${formatExpenseWithCurrency(
   218→            Number(item.calcAmount),
   219→            item.currency
   220→          )}!`,
   221→          [
   222→            //i18n.t("deleteAllExpenses"), i18n.t("deleteAllExpensesExt")
   223→            // The "No" button
   224→            // Does nothing but dismiss the dialog when tapped
   225→            {
   226→              text: i18n.t("no"),
   227→              onPress: async () => {
   228→                try {
   229→                  navigation?.popToTop();
   230→                  Toast.show({
   231→                    type: "loading",
   232→                    text1: i18n.t("toastDeleting1"),
   233→                    text2: i18n.t("toastDeleting2"),
   234→                    autoHide: false,
   235→                  });
   236→                  const item: OfflineQueueManageExpenseItem = {
   237→                    type: "delete",
   238→                    expense: {
   239→                      tripid: tripID,
   240→                      uid: uid,
   241→                      id: editedExpenseId,
   242→                    },
   243→                  };
   244→                  expenseCtx?.deleteExpense(editedExpenseId);
   245→                  await deleteExpenseOnlineOffline(item, isOnline);
   246→                  await touchAllTravelers(tripID, true);
   247→                  Toast.hide();
   248→                } catch (error) {
   249→                  // console.log(i18n.t("deleteError"), error);
   250→                  Toast.show({
   251→                    text1: i18n.t("error"),
   252→                    text2: i18n.t("error2"),
   253→                    type: "error",
   254→                  });
   255→                }
   256→              },
   257→            },
   258→            // The "Yes" button
   259→            {
   260→              text: i18n.t("yes"),
   261→              onPress: async () => {
   262→                await deleteAllExpenses();
   263→                return;
   264→              },
   265→            },
   266→          ]
   267→        );
   268→      } else {
   269→        // single id
   270→        try {
   271→          navigation?.popToTop();
   272→          Toast.show({
   273→            type: "loading",
   274→            text1: i18n.t("toastDeleting1"),
   275→            text2: i18n.t("toastDeleting2"),
   276→            autoHide: false,
   277→          });
   278→          const item: OfflineQueueManageExpenseItem = {
   279→            type: "delete",
   280→            expense: {
   281→              tripid: tripID,
   282→              uid: uid,
   283→              id: editedExpenseId,
   284→            },
   285→          };
   286→          expenseCtx?.deleteExpense(editedExpenseId);
   287→          await deleteExpenseOnlineOffline(item, isOnline);
   288→          await touchAllTravelers(tripID, true);
   289→          Toast.hide();
   290→        } catch (error) {
   291→          // console.log(i18n.t("deleteError"), error);
   292→          Toast.show({
   293→            text1: i18n.t("error"),
   294→            text2: i18n.t("error2"),
   295→            type: "error",
   296→          });
   297→        }
   298→      }
   299→    }
   300→    async function deleteExpenseHandler() {
   301→      Alert.alert(i18n.t("sure"), i18n.t("sureExt"), [
   302→        // The "No" button
   303→        // Does nothing but dismiss the dialog when tapped
   304→        {
   305→          text: i18n.t("no"),
   306→          onPress: () => forceCloseRow(index),
   307→        },
   308→        // The "Yes" button
   309→        {
   310→          text: i18n.t("yes"),
   311→          onPress: () => {
   312→            deleteExp();
   313→          },
   314→        },
   315→      ]);
   316→    }
   317→    deleteExpenseHandler();
   318→  }, []);
   319→
   320→  const renderExpenseItem = useCallback(
   321→    (
   322→      isOnline: boolean,
   323→      selectable: boolean,
   324→      selected: ExpenseData[],
   325→      selectItem: (item: ExpenseData, id: object) => void,
   326→      setSelectable: (selectable: boolean) => void,
   327→      expensesLength: number,
   328→      itemData
   329→    ) => {
   330→      const id = itemData.item.id || null;
   331→      if (
   332→        id &&
   333→        itemData.item.id[0] === "s" &&
   334→        itemData.item.id[1] === "h" &&
   335→        itemData.item.id[2] === "a" &&
   336→        itemData.item.id[3] === "d" &&
   337→        itemData.item.id[4] === "o" &&
   338→        itemData.item.id[5] === "w"
   339→      )
   340→        return (
   341→          <View
   342→            style={{ height: dynamicScale(56, true), width: "100%" }}
   343→          ></View>
   344→        );
   345→      const index = itemData.index;
   346→      const navigateToExpense = async () => {
   347→        Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
   348→        navigation.navigate("ManageExpense", {
   349→          expenseId: itemData.item.id,
   350→        });
   351→      };
   352→      if (expensesLength > MAX_EXPENSES_RENDER) {
   353→        // show cheap JSX
   354→        return (
   355→          <View>
   356→            <TouchableOpacity
   357→              style={[
   358→                styles.fastExpenseContainer,
   359→                {
   360→                  height: constantScale(55),
   361→                  paddingLeft: dynamicScale(16),
   362→                  paddingRight: dynamicScale(12),
   363→                },
   364→              ]}
   365→              onPress={() => navigateToExpense()}
   366→            >
   367→              <View>
   368→                {/* {selectable && cheapSelectableJSX} */}
   369→                <Text style={styles.descriptionText}>
   370→                  {itemData.item?.description}
   371→                </Text>
   372→                <Text style={styles.secondaryText}>
   373→                  {toShortFormat(itemData.item?.date)}{" "}
   374→                </Text>
   375→              </View>
   376→              <Text style={styles.fastExpenseText}>
   377→                {formatExpenseWithCurrency(
   378→                  itemData.item?.amount || 0,
   379→                  itemData.item?.currency
   380→                )}
   381→              </Text>
   382→            </TouchableOpacity>
   383→          </View>
   384→        );
   385→      }
   386→      const selectableJSX = (
   387→        <Animated.View
   388→          entering={FadeInLeft}
   389→          exiting={FadeOutLeft}
   390→          style={{
   391→            flex: 0,
   392→            position: "absolute",
   393→            top: -36,
   394→            left: -46,
   395→            zIndex: 1,
   396→          }}
   397→        >
   398→          <IconButton
   399→            icon={
   400→              selected.includes(itemData.item.id)
   401→                ? "ios-checkmark-circle"
   402→                : "ellipse-outline"
   403→            }
   404→            color={
   405→              selected.includes(itemData.item.id)
   406→                ? GlobalStyles.colors.textColor
   407→                : GlobalStyles.colors.gray700
   408→            }
   409→            size={constantScale(16, 0.5)}
   410→            onPress={selectItem.bind(this, itemData.item.id)}
   411→            buttonStyle={{ padding: dynamicScale(48, false, 0.5) }}
   412→          ></IconButton>
   413→        </Animated.View>
   414→      );
   415→      if (Platform.OS === "android")
   416→        return (
   417→          <View
   418→            style={{
   419→              height: constantScale(55),
   420→              width: "100%",
   421→              backgroundColor: GlobalStyles.colors.backgroundColor,
   422→            }}
   423→          >
   424→            <GestureHandlerRootView>
   425→              <Swipeable
   426→                renderLeftActions={(progress, dragX) =>
   427→                  renderRightActions(
   428→                    progress,
   429→                    dragX,
   430→                    onClick.bind(this, itemData, isOnline)
   431→                  )
   432→                }
   433→                onSwipeableOpen={closeRow.bind(this, index)}
   434→                ref={(ref) => (row[index] = ref)}
   435→                overshootFriction={8}
   436→              >
   437→                {selectable && selectableJSX}
   438→                <MemoizedExpenseItem
   439→                  showSumForTravellerName={travellerName}
   440→                  filtered={filtered}
   441→                  setSelectable={setSelectable}
   442→                  selectItem={selectItem}
   443→                  {...itemData.item}
   444→                />
   445→              </Swipeable>
   446→            </GestureHandlerRootView>
   447→          </View>
   448→        );
   449→      //else platform ios
   450→      return (
   451→        <View style={{ height: constantScale(55), width: "100%" }}>
   452→          <Swipeable
   453→            renderRightActions={(progress, dragX) =>
   454→              renderRightActions(
   455→                progress,
   456→                dragX,
   457→                onClick.bind(this, itemData, isOnline)
   458→              )
   459→            }
   460→            onSwipeableOpen={closeRow.bind(this, index)}
   461→            ref={(ref) => (row[index] = ref)}
   462→            rightOpenValue={-100}
   463→            disableLeftSwipe={true}
   464→            overshootFriction={8}
   465→          >
   466→            {selectable && selectableJSX}
   467→            <MemoizedExpenseItem
   468→              showSumForTravellerName={travellerName}
   469→              filtered={filtered}
   470→              setSelectable={setSelectable}
   471→              selectItem={selectItem}
   472→              {...itemData.item}
   473→            />
   474→          </Swipeable>
   475→        </View>
   476→      );
   477→    },
   478→    [closeRow, travellerName, filtered, renderRightActions, onClick, row]
   479→  );
   480→
   481→  const scrollTo = useCallback(
   482→    (index: number, animated = true) => {
   483→      if (!flatListRef.current || index > expenses?.length + 1) return;
   484→      if (flatListRef.current) {
   485→        flatListRef.current.scrollToIndex({
   486→          index: index,
   487→          animated: animated,
   488→        });
   489→      }
   490→    },
   491→    [expenses?.length]
   492→  );
   493→  const scrollToTopHandler = useCallback(() => {
   494→    scrollTo(1);
   495→  }, [scrollTo]);
   496→
   497→  useEffect(() => {
   498→    scrollTo(1);
   499→  }, [scrollTo, periodName]);
   500→
   501→  const selectItem = (item) => {
   502→    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
   503→    if (selected.includes(item)) {
   504→      setSelected(selected.filter((newItem) => newItem !== item));
   505→    } else {
   506→      setSelected([...selected, item]);
   507→    }
   508→    // console.log("selected", selected);
   509→  };
   510→  const selectAll = useCallback(() => {
   511→    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
   512→    const filteredExpenses = expenses.filter(
   513→      (item) => !item.id.includes("shadow")
   514→    );
   515→    requestAnimationFrame(() => {
   516→      if (selected?.length === filteredExpenses?.length) {
   517→        setSelected([]);
   518→      } else {
   519→        setSelected(filteredExpenses.map((item) => item.id));
   520→      }
   521→    });
   522→  }, [expenses?.length, selected?.length]);
   523→
   524→  const finderWithExpenses = useCallback(() => {
   525→    Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
   526→    if (selected.length === 0) return;
   527→    const finderExpenses = expenses.filter(
   528→      (item) => selected.includes(item.id) && !item.id.includes("shadow")
   529→    );
   530→    navigation.navigate("FilteredPieCharts", {
   531→      expenses: finderExpenses,
   532→      dayString: `${finderExpenses?.length} selected Expenses from ${periodName}`,
   533→      noList: true,
   534→    });
   535→  }, [expenses?.length, periodName, selected?.length]);
   536→
   537→  const [allTrips, setAllTrips] = useState<TripAsObject[]>([]);
   538→  useEffect(() => {
   539→    async function asyncSetAllTrips() {
   540→      if (!userCtx.tripHistory) return;
   541→      const allTripsAsObjects: TripAsObject[] = [];
   542→
   543→      for (let i = 0; i < userCtx.tripHistory.length; i++) {
   544→        const tripid = userCtx.tripHistory[i];
   545→        const tripName = await fetchTripName(tripid);
   546→        allTripsAsObjects.push({
   547→          tripid: tripid,
   548→          tripname: tripName,
   549→          selected: true,
   550→        });
   551→      }
   552→      setAllTrips(allTripsAsObjects);
   553→    }
   554→    asyncSetAllTrips();
   555→  }, [userCtx.tripHistory?.length]);
   556→
   557→  const handleEditMultipleExpenses = useCallback(async () => {
   558→    if (!selected || selected.length === 0) return;
   559→    if (selected.length === 1) {
   560→      navigation.navigate("ManageExpense", { expenseId: selected[0] });
   561→    }
   562→    // else we have an array of expenseIds which we want to simultaneously edit
   563→    // TODO: implement this in ManageMultipleExpenses.tsx and App.tsx
   564→    // navigation.navigate("ManageMultipleExpenses", { multipleIds: selected });
   565→  }, [selected.length]);
   566→
   567→  const handleMoveToTripPress = useCallback(
   568→    async (_tripid, _tripname) => {
   569→      // console.log("handleMoveToTripPress ~ tripid", _tripid);
   570→      // console.log("handleMoveToTripPress ~ tripname", _tripname);
   571→      Toast.show({
   572→        type: "loading",
   573→        text1: `Moving ${selected?.length} expenses`,
   574→        text2: `from ${tripName} to ${_tripname}!`,
   575→        autoHide: false,
   576→      });
   577→      navigation?.popToTop();
   578→
   579→      for (let i = 0; i < selected?.length; i++) {
   580→        const expenseData = expenses.find((item) => item.id === selected[i]);
   581→        try {
   582→          const expenseUid = expenseData.uid;
   583→          const expenseId = expenseData.id;
   584→          expenseData.date = expenseData.startDate;
   585→          const itemToCreate: OfflineQueueManageExpenseItem = {
   586→            type: "add",
   587→            expense: {
   588→              tripid: _tripid,
   589→              uid: expenseUid,
   590→              expenseData: expenseData,
   591→            },
   592→          };
   593→          const itemToDelete: OfflineQueueManageExpenseItem = {
   594→            type: "delete",
   595→            expense: {
   596→              tripid: tripID,
   597→              uid: expenseUid,
   598→              id: expenseId,
   599→            },
   600→          };
   601→          await storeExpenseOnlineOffline(itemToCreate, isOnline, _tripid);
   602→          expenseCtx?.deleteExpense(expenseId);
   603→          await deleteExpenseOnlineOffline(itemToDelete, isOnline);
   604→          await touchAllTravelers(tripID, true);
   605→          await touchAllTravelers(_tripid, true);
   606→          Toast.hide();
   607→        } catch (error) {
   608→          safeLogError(error);
   609→          Toast.show({
   610→            text1: i18n.t("error"),
   611→            text2: i18n.t("error2"),
   612→            type: "error",
   613→          });
   614→        }
   615→      }
   616→      setSelected([]);
   617→      setSelectable(false);
   618→    },
   619→    [selected.length, expenses?.length, tripID, tripName]
   620→  );
   621→
   622→  const moveSelectedToTrip = useCallback(() => {
