   657→        //i18n.t("deleteAllExpenses"), i18n.t("deleteAllExpensesExt")
   658→        // The "No" button
   659→        // Does nothing but dismiss the dialog when tapped
   660→        {
   661→          text: i18n.t("cancel"),
   662→          onPress: () => {
   663→            return;
   664→          },
   665→        },
   666→        // The "Yes" button
   667→        {
   668→          text: i18n.t("delete"),
